import { Component } from 'react';
import {useState , useEffect} from "react";
import Axios from "axios";


function Filiere(){

    const [ nom , setNom ] = useState('');
    const [ prenom , setPrenom ] = useState('');
    const [ massHoraire , setMh ] = useState('');
    const [ codeSalle , setSalleF ] = useState('');
    const [salleList ,  setSalleList] = useState([])
    const [formateursList ,  setFormateursList] = useState([])

    useEffect(()=>{
        Axios.get("http://localhost:3001/api/select/codesSalles").then((response)=> {
            setSalleList(response.data)
        })
    },[])

    useEffect(()=>{
        Axios.get("http://localhost:3001/api/select/formateurs").then((response)=> {
            setFormateursList(response.data)
        })
    },[])

    const submitReview = () => {
        Axios.post("http://localhost:3001/api/insert/formateur",{
            nom: nom,
            prenom: prenom,
            massHoraire: massHoraire,
            codeSalle: codeSalle
        })
        setFormateursList([...formateursList,{nom: nom,
            prenom: prenom,
            massHoraire: massHoraire,
            codeSalle: codeSalle}])
        
    }

    const deleteFormateur = (idFormateur) => {
        Axios.delete(`http://localhost:3001/api/delete/formateur/${idFormateur}`)
        
        
    }
    return(
        <div className="col-sm-11 mt-4">
            <div  className="container-fluid" id="content-add-filiere">
                <div className="row">
                    <div className="col-sm-12 dashFiliere">
                        <div className="addTFiliereFormWrapper">
                            <span id="message"></span>
                            <form method="POST" id="filiereForm">
                                <div className="form-floating mb-3">
                                    <input type="text" className="form-control" id="codeFil" name="codeFil"/>
                                    <label for="codeFil">Code de Filiere</label>
                                </div>
                                <div className="form-floating mb-3">
                                    <input type="text" className="form-control" id="nomFil" name="nomFil"/>
                                    <label for="nomFil">Nom de Filiere</label>
                                </div>
                                <div className="input-group mb-3">
                                    <button type="button" className="btn btn-success mx-3" id="submitFiliere" name="ajouterSalle"  onclick="save_filiere();return false;">Ajouter Filiére</button>
                                </div>
                            </form>
                        </div>
                    </div>
                    <div className='col-sm-12 mainFiliere'>
                        <div id="filiereList">
                            
                            
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
    
}

export default Filiere;
