import { Component } from 'react';




function Salles(){
    
    const [ codeSalle , setCodeSalle ] = useState('');
    const [ typeSalle , setTypeSalle ] = useState('');
    const [ sallesList , setSallesList ] = useState([]);
    const [ infoSallesList , setInfoSallesList ] = useState([]);
    const [ normalSallesList , setNormalSallesList] = useState([]);
    const [ atelierList , SetAtelierList] = useState([]);

    const submitModule = () => {
        Axios.post("http://localhost:3001/api/insert/salle",{
            codeSalle: codeSalle,
            typeSalle: typeSalle
            
        })
        setSallesList([...sallesList,{codeSalle: codeSalle,
            typeSalle: typeSalle}])
        
    }
 

    return(
        <div  className="container-fluid" id="content-add-salle">
            <div className="row">
                <div className="col-sm-12 dashSalle">
                    <div className="addTeacherFormWrapper">
                        <span id="message"></span>
                        <form method="POST" id="salleForm">
                            <div className="form-floating my-3">
                                <input type="text" className="form-control" id="codeS" name="codeS"/>
                                <label for="nomF">Code de la salle</label>
                            </div>
                            <select className="form-select form-control my-3" aria-label="Default select example" name="typeS">
                                <option selected>Choisir Quelle type de salle s'agit elle :</option>
                                <option value="normale">Salle Normale</option>
                                <option value="info">Salle Informatique</option>
                                <option value="atelier">Atelier</option>
                            </select>
                            <div className="input-group mb-3">
                                <button type="button" className=" my-3 btn btn-success mx-3" id="submit" name="ajouterSalle"  onclick="save_className();return false;">Ajouter La salle</button>
                            </div>
                        </form>
                    </div>
                </div>
                <div className='col-sm-11 mainSalle'>
                    <div id="salleList">
                    
                    </div>
                </div>
            </div>
        </div>
    )
    
}

export default Salles;
