import {useState , useEffect} from "react";
import Axios from "axios";


function Filiere(){

    const [ codeFiliere , setCodeFiliere ] = useState('');
    const [ nomFiliere , SetNomFiliere ] = useState('');

    const [filiereList ,  setFiliereList] = useState([])



    useEffect(()=>{
        Axios.get("http://localhost:3001/api/select/filieres").then((response)=> {
            setFiliereList(response.data)
        })
    },[])

    const submitReview = () => {
        Axios.post("http://localhost:3001/api/insert/filiere",{
            codeFiliere: codeFiliere,
            nomFiliere: nomFiliere
        })
        setFiliereList([...filiereList,{codeFiliere: codeFiliere,
            nomFiliere: nomFiliere}])
        
    }

    const deleteFiliere = (codeFiliere) => {
        Axios.delete(`http://localhost:3001/api/delete/formateur/${codeFiliere}`)
        
        
    }
    return(
        <div className="col-sm-11 mt-4">
            <div  className="container-fluid" id="content-add-filiere">
                <div className="row">
                    <div className="col-sm-12 dashFiliere">
                        <div className="addTFiliereFormWrapper">
                            <span id="message"></span>
                            <form method="POST" id="filiereForm">
                                <div className="form-floating mb-3">
                                    <input type="text" className="form-control" id="codeFil" name="codeFil" onChange={(e) => {
                                    setCodeFiliere(e.target.value)
                                }}/>
                                    <label for="codeFil">Code de Filiere</label>
                                </div>
                                <div className="form-floating mb-3">
                                    <input type="text" className="form-control" id="nomFil" name="nomFil" onChange={(e) => {
                                    SetNomFiliere(e.target.value)
                                }}/>
                                    <label for="nomFil">Nom de Filiere</label>
                                </div>
                                <div className="input-group mb-3">
                                    <button type="button" className="btn btn-success mx-3" id="submitFiliere" name="ajouterSalle"  onclick={submitReview}>Ajouter Filiére</button>
                                </div>
                            </form>
                        </div>
                    </div>
                    <div className='col-sm-12 mainFiliere'>
                        <div id="filiereList">
                            
                        <table className="table table-hover table-dark table-striped">
                        <thead>
                            <tr>
                                <td>code filiere</td>
                                <td>intitulé</td>
                                <td>Supprimer</td>
                                <td>Modifier</td>
                            </tr>
                        </thead>
                        <tbody>
                        {filiereList.map((fil) =>(
                            <tr key={fil.codeFiliere}>
                                <td> {fil.codeFiliere} </td>
                                <td> {fil.nomFiliere} </td>
                                
                                <td><i class='fa-solid fa-trash-can text-danger btn btn-danger text-light' onClick={() => deleteFiliere(fil.codeFiliere)}></i></td>
                                <td><i class='fa-solid fa-arrows-rotate btn btn-success text-light'></i></td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
    
}

export default Filiere;
