import { useState } from "react";
import CoursList from "./courseList";

const Home = () => {
    const [courses , setCourses] = useState([
        {module : "React" , file :"coursReact.pdf" , prof : "Filali" , id : 1},
        {module : "Scrum" , file :"coursScrum.pdf" , prof : "Wiam" , id : 2},
        {module : "mySQl" , file :"coursMySQL.pdf" , prof : "Harmouchi" , id : 3},
        {module : "Figma" , file :"coursFigma.pdf" , prof : "Essofi" , id : 4},
        {module : "Laravel" , file :"coursLaravel.pdf" , prof : "Wiam" , id : 5}
    ])
    const handleDelete = (id) => {
        const newCourses = courses.filter( (course) => { newCourses.id !== id })
        setCourses(newCourses)
    }
    return (
        <div className="home">
            <CoursList courses = { courses } title="all courses" handleDelete= { handleDelete } />
            <CoursList courses = { courses.filter( (course) => course.prof === "Wiam" ) } title = "wiam courses" />

        </div>
    )
}
export default Home
