import { useState , useEffect } from "react";
import CoursList from "./courseList";

const Home = () => {
    const [courses , setCourses] = useState(null)
    const handleDelete = (id) => {
        const newCourses = courses.filter( course=> course.id !== id )
        setCourses(newCourses)
    }
    useEffect(()=>{
        fetch('http://localhost:5000/courses')
            .then(res =>{
                return res.json()
            })
            .then(data => {
                setCourses(data)
            })
    },[])
    return (
        <div className="home">
            {<CoursList courses = { courses } title="all courses" handleDelete= {handleDelete} />}
            {<CoursList courses = { courses.filter( (course) => course.prof === "Wiam" ) } title = "wiam courses" />}

        </div>
    )
}
export default Home
