import {useState , useEffect} from "react";
import Axios from "axios";


function SallesDispo(){
    const [ chosenDay , setChosenDay ] = useState('');
    const [ dispoSalles , setDispoSalles ] = useState([]);
    const [ filteredDispoSalles , setFilteredDispoSalles ] = useState([]);

    useEffect(()=>{
        Axios.get("http://localhost:3001/api/select/salleDispo").then((response)=> {
            setDispoSalles(response.data)
        })
    },[])


    const getSalles = (e) => {
        dispoSalles.filter((salle) => (
            setFilteredDispoSalles(salle.jours == e.innerHTML)
        ))
    }

    return(
        <div className="container-fluid">
            <div className="row">
                <div className="col-sm-12 salleDispoDash">
                    <form id="salleDispoForm" method="POST">
                        <div className='DaysWrapper'>
                            <button className='days btn btn-outline-primary btn-light' onClick={getSalles}>Lundi</button>
                            <button className='days btn btn-outline-primary btn-light' onClick={getSalles}>Mardi</button>
                            <button className='days btn btn-outline-primary btn-light' onClick={getSalles}>Mercredi</button>
                        </div>
                        <div className='DaysWrapper'>
                            <button className='days btn btn-outline-primary btn-light' onClick={getSalles}>Jeudi</button>
                            <button className='days btn btn-outline-primary btn-light' onClick={getSalles}>Vendredi</button>
                            <button className='days btn btn-outline-primary btn-light' onClick={getSalles}>Samedi</button>
                        </div>

                    
                    </form>
                </div>
                <div className="col-sm-12 mainSalleDispo">
                    <div id="message1"></div>
                    
                </div>
            </div>
        </div>
    )

}

export default SallesDispo;
