import {useState , useEffect} from "react";
import Axios from "axios";

export default function SetEmlploi (){
    const [groupeList ,  setGroupeList] = useState([])

    const [monday1List ,  setMonday1List] = useState([{nom:"",prenom:""}])
    const [monday2List ,  setMonday2List] = useState([{nom:"",prenom:""}])
    const [monday3List ,  setMonday3List] = useState([{nom:"",prenom:""}])
    const [monday4List ,  setMonday4List] = useState([{nom:"",prenom:""}])

    const [tuesday1List ,  setTuesday1List] = useState([{nom:"",prenom:""}])
    const [tuesday2List ,  setTuesday2List] = useState([{nom:"",prenom:""}])
    const [tuesday3List ,  setTuesday3List] = useState([{nom:"",prenom:""}])
    const [tuesday4List ,  setTuesday4List] = useState([{nom:"",prenom:""}])

    const [wedensday1List ,  setWedensday1List] = useState([{nom:"",prenom:""}])
    const [wedensday2List ,  setWedensday2List] = useState([{nom:"",prenom:""}])
    const [wedensday3List ,  setWedensday3List] = useState([{nom:"",prenom:""}])
    const [wedensday4List ,  setWedensday4List] = useState([{nom:"",prenom:""}])

    const [thursday1List ,  setThursday1List] = useState([{nom:"",prenom:""}])
    const [thursday2List ,  setThursday2List] = useState([{nom:"",prenom:""}])
    const [thursday3List ,  setThursday3List] = useState([{nom:"",prenom:""}])
    const [thursday4List ,  setThursday4List] = useState([{nom:"",prenom:""}])

    const [friday1List ,  setFriday1List] = useState([{nom:"",prenom:""}])
    const [friday2List ,  setFriday2List] = useState([{nom:"",prenom:""}])
    const [friday3List ,  setFriday3List] = useState([{nom:"",prenom:""}])
    const [friday4List ,  setFriday4List] = useState([{nom:"",prenom:""}])

    const [saturday1List ,  setSaturday1List] = useState([{nom:"",prenom:""}])
    const [saturday2List ,  setSaturday2List] = useState([{nom:"",prenom:""}])
    const [saturday3List ,  setSaturday3List] = useState([{nom:"",prenom:""}])
    const [saturday4List ,  setSaturday4List] = useState([{nom:"",prenom:""}])

    const [ groupeChoisis , setGroupeChoisis ] = useState("")


    useEffect(()=>{
        Axios.get("http://localhost:3001/api/select/groupe").then((response)=> {
            setGroupeList(response.data)
        })
    })


    

    useEffect(()=>{
        Axios.get(`http://localhost:3001/api/select/cours/monday/1/${groupeChoisis}`).then((response)=> {
            setMonday1List(response.data)

        })
        Axios.get(`http://localhost:3001/api/select/cours/monday/2/${groupeChoisis}`).then((response)=> {
            setMonday2List(response.data)

        })
        Axios.get(`http://localhost:3001/api/select/cours/monday/3/${groupeChoisis}`).then((response)=> {
            setMonday3List(response.data)

        })
        Axios.get(`http://localhost:3001/api/select/cours/monday/4/${groupeChoisis}`).then((response)=> {
            setMonday4List(response.data)

        })
    })

    

    


    return(
        <div className="container">
            <div className="row justify-content-center">
                <form>
                    <select onChange = {(e) => { setGroupeChoisis(e.target.value)}}>
                    {groupeList.map((g) =>(
                        <option key={g.codeGroupe} value={g.codeGroupe} > {g.codeGroupe} </option>
                    ))}
                    </select>
                </form>
            </div>
            <div className="row justify-content-center">
                <table className="table table-bordered">
                    <thead>
                        <tr>
                            <th>  </th>
                            <th> <span className="start">08:30</span> <span className="end">10:50</span>  </th>
                            <th> <span className="start">11:10</span> <span className="end">13:15</span>  </th>
                            <th> <span className="start">13:30</span> <span className="end">15:50</span>  </th>
                            <th> <span className="start">16:10</span> <span className="end">18:30</span>  </th>
                        </tr>
                    </thead>

                        <tbody>
                        <tr>
                            <td>Lundi</td>
                            <td id="l1"><h4>{monday1List[0].nom} {monday1List[0].prenom}</h4><h5>{monday1List[0].codeSalle}</h5><h6>{monday1List[0].codeModule}</h6></td>
                            <td id="l2"><h4>{monday2List[0].nom} {monday2List[0].prenom}</h4><h5>{monday2List[0].codeSalle}</h5><h6>{monday2List[0].codeModule}</h6></td>
                            <td id="l3"><h4>{monday3List[0].nom} {monday3List[0].prenom}</h4><h5>{monday3List[0].codeSalle}</h5><h6>{monday3List[0].codeModule}</h6></td>
                            <td id="l4"><h4>{monday4List[0].nom} {monday4List[0].prenom}</h4><h5>{monday4List[0].codeSalle}</h5><h6>{monday4List[0].codeModule}</h6></td>
                        </tr>
                        <tr>
                            <td>Mardi</td>
                            <td id="m1"><h4>{tuesday1List[0].nom} {tuesday1List[0].prenom}</h4><h5>{tuesday1List[0].codeSalle}</h5><h6>{tuesday1List[0].codeModule}</h6></td>
                            <td id="m2"><h4>{tuesday2List[0].nom} {tuesday2List[0].prenom}</h4><h5>{tuesday2List[0].codeSalle}</h5><h6>{tuesday2List[0].codeModule}</h6></td>
                            <td id="m3"><h4>{tuesday3List[0].nom} {tuesday3List[0].prenom}</h4><h5>{tuesday3List[0].codeSalle}</h5><h6>{tuesday3List[0].codeModule}</h6></td>
                            <td id="m4"><h4>{tuesday4List[0].nom} {tuesday4List[0].prenom}</h4><h5>{tuesday4List[0].codeSalle}</h5><h6>{tuesday4List[0].codeModule}</h6></td>
                        </tr>
                        <tr>
                            <td>Mercredi</td>
                            <td id="me1"><h4>{wedensday1List[0].nom} {wedensday1List[0].prenom}</h4><h5>{wedensday1List[0].codeSalle}</h5><h6>{wedensday1List[0].codeModule}</h6></td>
                            <td id="me2"><h4>{wedensday2List[0].nom} {wedensday2List[0].prenom}</h4><h5>{wedensday2List[0].codeSalle}</h5><h6>{wedensday2List[0].codeModule}</h6></td>
                            <td id="me3"><h4>{wedensday3List[0].nom} {wedensday3List[0].prenom}</h4><h5>{wedensday3List[0].codeSalle}</h5><h6>{wedensday3List[0].codeModule}</h6></td>
                            <td id="me4"><h4>{wedensday4List[0].nom} {wedensday4List[0].prenom}</h4><h5>{wedensday4List[0].codeSalle}</h5><h6>{wedensday4List[0].codeModule}</h6></td>
                        </tr>
                        <tr>
                            <td>Jeudi</td>
                            <td id="j1"><h4>{thursday1List[0].nom} {thursday1List[0].prenom}</h4><h5>{thursday1List[0].codeSalle}</h5><h6>{thursday1List[0].codeModule}</h6></td>
                            <td id="j2"><h4>{thursday2List[0].nom} {thursday2List[0].prenom}</h4><h5>{thursday2List[0].codeSalle}</h5><h6>{thursday2List[0].codeModule}</h6></td>
                            <td id="j3"><h4>{thursday3List[0].nom} {thursday3List[0].prenom}</h4><h5>{thursday3List[0].codeSalle}</h5><h6>{thursday3List[0].codeModule}</h6></td>
                            <td id="j4"><h4>{thursday4List[0].nom} {thursday4List[0].prenom}</h4><h5>{thursday4List[0].codeSalle}</h5><h6>{thursday4List[0].codeModule}</h6></td>
                        </tr>
                        <tr>
                            <td>Vendredi</td>
                            <td id="v1"><h4>{friday1List[0].nom} {friday1List[0].prenom}</h4><h5>{friday1List[0].codeSalle}</h5><h6>{friday1List[0].codeModule}</h6></td>
                            <td id="v2"><h4>{friday2List[0].nom} {friday2List[0].prenom}</h4><h5>{friday2List[0].codeSalle}</h5><h6>{friday2List[0].codeModule}</h6></td>
                            <td id="v3"><h4>{friday3List[0].nom} {friday3List[0].prenom}</h4><h5>{friday3List[0].codeSalle}</h5><h6>{friday3List[0].codeModule}</h6></td>
                            <td id="v4"><h4>{friday4List[0].nom} {friday4List[0].prenom}</h4><h5>{friday4List[0].codeSalle}</h5><h6>{friday4List[0].codeModule}</h6></td>
                        </tr>
                        <tr>
                            <td>Samedi</td>
                            <td id="s1"><h4>{saturday1List[0].nom} {saturday1List[0].prenom}</h4><h5>{saturday1List[0].codeSalle}</h5><h6>{saturday1List[0].codeModule}</h6></td>
                            <td id="s2"><h4>{saturday2List[0].nom} {saturday2List[0].prenom}</h4><h5>{saturday2List[0].codeSalle}</h5><h6>{saturday2List[0].codeModule}</h6></td>
                            <td id="s3"><h4>{saturday3List[0].nom} {saturday3List[0].prenom}</h4><h5>{saturday3List[0].codeSalle}</h5><h6>{saturday3List[0].codeModule}</h6></td>
                            <td id="s4"><h4>{saturday4List[0].nom} {saturday4List[0].prenom}</h4><h5>{saturday4List[0].codeSalle}</h5><h6>{saturday4List[0].codeModule}</h6></td>
                        </tr>
                        </tbody>
      
                </table>
            </div>
        </div>
    )
}
