import { Component } from 'react';




function Groupes(){

    const [ codeGroupe , setCodeGroupe ] = useState('');
    const [ annee , setAnnee ] = useState('');
    const [filieres ,  setFilieres] = useState([])

    const [filiereList ,  setFiliereList] = useState([])



    useEffect(()=>{
        Axios.get("http://localhost:3001/api/select/filiere").then((response)=> {
            setFiliereList(response.data)
        })
    },[])

    const submitFiliere = () => {
        Axios.post("http://localhost:3001/api/insert/filiere",{
            codeFiliere: codeFiliere,
            nomFiliere: nomFiliere
        })
        
        setFiliereList([...filiereList,{codeFiliere: codeFiliere,
            nomFiliere: nomFiliere}])
        
    }

    const deleteFiliere = (codeFiliere) => {
        Axios.delete(`http://localhost:3001/api/delete/filiere/${codeFiliere}`)
        
        
    }
    return(
        <div className="mt-3">
            <div  className="container-fluid" id="content-add-group">
                <div className="row">
                    <div className="col-sm-11 dashGroupe">
                        <div className="addTeacherFormWrapper">
                            <span id="message"></span>
                            <form method="POST" id="groupeForm">
                                <div className="form-floating my-3">
                                    <input type="text" className="form-control" id="codeGr" name="codeGr"/>
                                    <label for="codeGr">Code de Groupe</label>
                                </div>
                                <select name="filiereGr" id="filiereGr" className="form-control my-3">
                                    <option disabled selected>Choisir un Filiere : </option>
                                    
                                </select>
                                <select name="anneeGr" id="anneeGr" className="form-control my-3">
                                    <option disabled selected>Choisir L'année de formation  : </option>
                                    <option value="1A">1ére Année</option>
                                    <option value="2A">2éme Année</option>
                                </select>
                                <div className="input-group my-3">
                                    <button type="button" className="btn btn-success mx-3" id="submit" name="ajouterGroupe"  onclick="save_groupe();return false;">Ajouter Groupe</button>
                                </div>
                            </form>
                        </div>
                    </div>
                    <div className="col-sm-11 mainGroup">
                        <div id="groupeList"  className="my-5">
                            
                            
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
    
}

export default Groupes;
