import {useState} from "react";


function NavBar(){
  const logo = "Youness OFPPT 2A"

  const toggleNav = () => {
    let nav = document.getElementsByClassName('links')
    nav = nav[0]
    nav = nav.style.display
    if(nav =="block"){
      nav = "none"
    }
  }

  return(
    <nav className="navigationMenu">
      <span> { logo }</span>
      <div className='links'>
        <a href='/'>Home</a>
        <a href='/react'>REACT JS</a>
        <a href='/laravel'>LARAVEL</a>
        <a href='/mySQL'>Procedural mySQL</a>
        <a href="/mongoDB">MongoDB</a>
        <a href="/uml">UML</a>
        <a href="/jira">JIRA</a>
        <a href="/Figma">FIGMA</a>
        <a href="/git">GIT / GITLAB</a>
        <a href="/mongoDB">MongoDB</a>
        <a href="/azure">AZURE</a>
      </div>
      <a className="toggleNav"><button onClick={toggleNav}>Toggle</button></a>

    </nav>
  )
}


export default NavBar ;
