import {useState , useEffect} from "react";
import Axios from "axios";

function Formateurs(){
    
    const [ nom , setNom ] = useState('');
    const [ prenom , setPrenom ] = useState('');
    const [ mh , setMh ] = useState('');
    const [ salleF , setSalleF ] = useState('');
    const [salleList ,  setSalleList] = useState([])

    useEffect(()=>{
        Axios.get("http://localhost:3001/api/select").then((response)=> {
            setSalleList(response.data)
        })
    },[])

    const submitReview = () => {
        Axios.post("http://localhost:3001/api/insert",{
            nom: nom,
            prenom: prenom,
            mh: mh,
            salleF: salleF
        }).then(()=>{
            alert("les données sont enrigestré avec success");
        })
    }

    return (
        
        <div  className="container-fluid" id="content-add-teacher">
            
            <div className="row">
                <div className="col-sm-12 dashFormateur">
                    <div className="addTeacherFormWrapper">
                        <center><span id="message"></span></center><br/>
                        <form method="POST" id="formateurForm">
                            <div className="form-floating ml-3">
                                <input type="text" className="form-control" id="nomF" name="nomF" onChange={(e) => {
                                    setNom(e.target.value)
                                }} />
                                <label for="nomF">Nom</label>
                            </div>
                            <div className="form-floating ml-3">
                                <input type="text" className="form-control" id="prenomF" name="prenomF" onChange={(e) => {
                                    setPrenom(e.target.value)
                                }}/>
                                <label for="prenomF">Prenom</label>
                            </div>
                            <div className="form-floating ml-3">
                                <input type="number" className="form-control" id="masseHoraireF" name="masseHoraireF" onChange={(e) => {
                                    setMh(e.target.value)
                                }}/>
                                <label for="masseHoraireF">Masse Horaire</label>
                            </div>
                            <select className="form-control" aria-label="Default select example" name="salleF" onChange={(e) => {
                                    setSalleF(e.target.value)
                                }} >
                                <option disabled>Choisir une salle pour le formateur/la formatrice :</option>
                                {salleList.forEach((salle) =>{
                                    <option value={salle.codeSalle}> 1{ salle.codeSalle } </option>
                                })}
                            
                            </select>
                            <div className="input-group ml-3">
                                <button type="button" className="btn btn-success w-50 mx-3" name="addTeacher" id="submit" onClick={ submitReview }> Ajouter Formateur/Formatrice</button>
                            </div>
                        </form>
                    </div>
                </div>
                <div className="col-sm-11 mainFormateur mt-5">

                </div>
            </div>
        </div>

    )
    
    
}

export default Formateurs;
