import {useState , useEffect} from "react";
import Axios from "axios";


function SallesDispo(){
    const [ chosenDay , setChosenDay ] = useState('');
    const [ dispoSalles , setDispoSalles ] = useState([]);
    const [ filteredDispoSalles , setFilteredDispoSalles ] = useState([]);

    useEffect(()=>{
        Axios.get("http://localhost:3001/api/select/salleDispo").then((response)=> {
            setDispoSalles(response.data)
        })
    },[])


    const getSalles = (e) => {
        dispoSalles.filter((salle) => (
            setFilteredDispoSalles(salle.jours == e.innerHTML)
        ))
    }

    return(
        <div className="container-fluid">
            <div className="row">
                <div className="col-sm-12 salleDispoDash">
                    <form id="salleDispoForm" >
                        <div className='DaysWrapper'>
                            <button className='days btn btn-outline-primary btn-light' onClick={(e) => getSalles(e.target.value)}>Lundi</button>
                            <button className='days btn btn-outline-primary btn-light' onClick={(e) => getSalles(e.target.value)}>Mardi</button>
                            <button className='days btn btn-outline-primary btn-light' onClick={(e) => getSalles(e.target.value)}>Mercredi</button>
                        </div>
                        <div className='DaysWrapper'>
                            <button className='days btn btn-outline-primary btn-light' onClick={(e) => getSalles(e.target.value)}>Jeudi</button>
                            <button className='days btn btn-outline-primary btn-light' onClick={(e) => getSalles(e.target.value)}>Vendredi</button>
                            <button className='days btn btn-outline-primary btn-light' onClick={(e) => getSalles(e.target.value)}>Samedi</button>
                        </div>

                    
                    </form>
                </div>
                <div className="col-sm-12 mainSalleDispo">
                    
                        <div className="container-fluid">
                          <div className="row">
                       { dispoSalles.map((ds)=>(
                          
                                    <div className="col-sm-1 "  key={ds.codeSalle}>
                                        <h6>{ds.codeSalle}</h6>
                                        <p>{ds.typeSalle}</p>
                                    </div>
                                
                        ))

                    }
                    </div>
                                
                    </div>
                </div>
            </div>
        </div>
    )

}

export default SallesDispo;
