import {useState , useEffect} from "react";
import Axios from "axios";
function  Modules(){

    const [ codeModule , setCodeModule ] = useState('');
    const [ titreModule , setTitreModule ] = useState('');
    const [ masseHoraire , setMasseHoraire ] = useState('');
    const [ codeFiliere , setCodeFiliere] = useState('');
    const [ codeGroupe , setCodeGroupe] = useState('');
    const [filieres ,  SetFilieres] = useState([])
    const [groupes ,  setGroupes] = useState([])
    const [modulesList ,  setModulesList] = useState([])



    useEffect(()=>{
        Axios.get("http://localhost:3001/api/select/filiere").then((response)=> {
            SetFilieres(response.data)
        })
    },[])

    

  

    useEffect(()=>{
        Axios.get(`http://localhost:3001/api/select/groupe/${codeFiliere}`).then((response)=> {
            setGroupes(response.data)
        })
    })

    

    useEffect(()=>{
        Axios.get("http://localhost:3001/api/select/module",{
            codeFiliere : codeFiliere,
            codeGroupe : codeGroupe
        }).then((response)=> {
            setModulesList(response.data)
        })
    },[])

    const submitModule = () => {
        Axios.post("http://localhost:3001/api/insert/module",{
            codeModule: codeModule,
            titreModule: titreModule,
            masseHoraire: masseHoraire
        })
        setModulesList([...modulesList,{codeModule: codeModule,
            titreModule: titreModule,
            masseHoraire: masseHoraire}])
        
    }

    const deleteFormateur = (idFormateur) => {
        Axios.delete(`http://localhost:3001/api/delete/formateur/${idFormateur}`)
        
        
    }
    return(
        <div className="mt-3">
            <div  className="container-fluid" id="content-add-module">
                <div className="row">
                    <div className="col-sm-11 dashModule">

                        <div className="addTeacherFormWrapper">
                            <span id="message"></span>
                            <form method="POST" id="moduleForm">
                                <div className="form-floating my-3">
                                    <input type="text" className="form-control" id="codeM" name="codeM" onClick = {(e) => {
                                    setCodeModule(e.target.value)
                                }}/>
                                    <label for="nomF">Code de Module</label>
                                </div>
                                <div className="form-floating my-3">
                                    <input type="text" className="form-control" id="titreM" name="titreM" onChange = {(e) => {
                                    setTitreModule(e.target.value)
                                }}/>
                                    <label for="prenomF">Intitulé de Module</label>
                                </div>
                                <div className="form-floating my-3">
                                    <input type="number" className="form-control" id="masseHoraireM" name="masseHoraireM" onChange= {(e) => {
                                    setMasseHoraire(e.target.value)
                                }}/>
                                    <label for="masseHoraireF">Masse Horaire de Module</label>
                                </div>
                                <select className="form-control my-3"  aria-label="Default select example" id="filiereM" onChange = {(e) => {
                                    setCodeFiliere(e.target.value)
                                    Axios.post(`htp://localhost:3001/api/goto/groupe/${e.target.value}`,{
                                        codeFiliere: codeFiliere
                                        
                                    })
                                    }}>
                                    <option disabled>Choisir le/les filiére de ce Module :</option>
                                    {filieres.map((fil) =>(
                                        <option key={fil.codeFiliere} value={fil.codeFiliere}> {fil.nomFiliere} </option>
                                    ))}
                                
                                </select>
                                <select className="form-control my-3"  aria-label="Default select example" name="groupeM" onChange = {(e) => {
                                    setCodeGroupe(e.target.value)
                                }}>
                                    <option disabled>Choisir le/les groupe de ce Module :</option>
                                    {groupes.map((g) =>(
                                        <option key={g.codeGroupe} value={g.codeGroupe}> {g.codeGroupe} </option>
                                    ))}
                                
                                </select>
                                <div className="input-group my-3">
                                    <button type="button" className="btn btn-success mx-3" name="addTeacher" id="submit" onClick={submitModule}> Ajouter ce Module</button>
                                </div>
                            </form>
                        </div>
                    </div>
                    <div className="col-sm-11 mainModule mt-5">
                        <form  method="POST" id="getModuleForm">
                            <select className="form-select form-control-1" aria-label="Default select example" name="getfiliereM" id="filieres" size="4">
                                <option disabled Selected>choisir les semaines durant lesquelles ce cours aura lieu :</option>

                                
                            
                            </select>
                            <div className="input-group mb-3">
                                <button type="button" className="btn btn-success" id="submit1" name="voirModule" onclick="voir_module();return false;">Voir les Modules de ce filiére :</button>
                            </div>
                        </form>
                        <div id="message1"><center><p className="tempMsg">Section Vide <i className="fa-solid fa-exclamation-circle"></i></p> <p>SVP choisis une filiére</p> </center></div>
                    </div>
                </div>     
            </div>
        </div>
    )
    
}

export default Modules;
