import {useState} from "react";


function NavBar(){
  const logo = "Youness OFPPT 2A"

  const [toggle , hideNav] = useState('toggle')


  const toggleNav = () => {
    hideNav('hide')
    let nav = document.getElementsByClassName('links')
    nav = nav[0]
    nav = nav.style.display = "flex"
    let button = document.querySelector('.toggleNav button')
    button.innerHTML = "Hide"
    button.onClick = "hide()"
  }

  const hide = () => {
    let nav = document.getElementsByClassName('links')
    nav = nav[0]
    nav = nav.style.display = "none"
    let button = document.querySelector('.toggleNav button')
    button.innerHTML = "Toggle"
    button.onClick = "toggleNav()"
  }


  return(
    <nav className="navigationMenu">
      <span> { logo }</span>
      <div className='links'>
        <a href='/'>Home</a>
        <a href='/react'>REACT JS</a>
        <a href='/laravel'>LARAVEL</a>
        <a href='/mySQL'>Procedural mySQL</a>
        <a href="/mongoDB">MongoDB</a>
        <a href="/uml">UML</a>
        <a href="/jira">JIRA</a>
        <a href="/Figma">FIGMA</a>
        <a href="/git">GIT / GITLAB</a>
        <a href="/mongoDB">MongoDB</a>
        <a href="/azure">AZURE</a>
      </div>
      <a className="toggleNav"><button onClick={{toggle}}>Toggle</button></a>

    </nav>
  )
}


export default NavBar ;
