import { useState , useEffect } from "react";
import CoursList from "./courseList";

const Home = () => {
    const [courses , setCourses] = useState(null)
    const [isPending, setIsPending] = useState(true)

    useEffect(()=>{
        fetch('http://localhost:5000/courses')
            .then(res =>{
                return res.json()
            })
            .then(data => {
                setCourses(data)
                setIsPending(false)
            })
    },[])

    
    return (
        <div className="home">
            {isPending && <div className="text-secondary"> Loading ....</div>}
            {courses && <CoursList courses = { courses } title="all courses" />}
            {courses && <CoursList courses = { courses.filter( (course) => course.prof === "Wiam" ) } title = "wiam courses" />}

        </div>
    )
}
export default Home
